#include "HHMLSys/HHMLSys_Ntuple.h"

//---------------------------------------------------------------
HHMLSys_Ntuple::HHMLSys_Ntuple() {
}

//---------------------------------------------------------------
HHMLSys_Ntuple::~HHMLSys_Ntuple() {
}

//---------------------------------------------------------------
void HHMLSys_Ntuple::ReadFiles(const std::string& SamplePath, 
			       const std::string& TreeName)
{
  fChain = CxxUtils::make_unique<TChain>(TreeName.c_str());
  fChain -> Add(SamplePath.c_str());
}

//---------------------------------------------------------------
void HHMLSys_Ntuple::ReadSumWeight(const std::string& SamplePath)
{
  fChainSumWeight = CxxUtils::make_unique<TChain>("sumWeights");
  fChainSumWeight -> Add(SamplePath.c_str());

  fChainSumWeight->SetBranchAddress("dsid", &dsid, &b_dsid);
  fChainSumWeight->SetBranchAddress("isAFII", &isAFII, &b_isAFII);
  fChainSumWeight->SetBranchAddress("totalEventsWeighted", &totalEventsWeighted, &b_totalEventsWeighted);
  fChainSumWeight->SetBranchAddress("totalEvents", &totalEvents, &b_totalEvents);
}

